type.text:
{
    title: string;
    argTypes: {
        maxOverlayHeight: {
            control: string;
        };
        size: {
            control: string;
            options: SelectSize[];
        };
        placeholder: {
            control: string;
        };
    };
}

declarations.default:
{
  render: () => {
    return html` <style>
        table {
          border-collapse: collapse;
        }

        th {
          text-transform: capitalize;
        }
        th,
        td {
          padding: 4px 8px;
        }
        thead td {
          text-align: center;
        }
      </style>
      ${sizes.map(
        size => html`<h2>Size: ${size}</h2>
          <table>
            <thead>
              <tr>
                <td></td>
                ${disabledStates.map(
                  disabledState =>
                    html` <td class="${disabledState ? 'sb-disabled' : ''}">
                      ${disabledState ? 'Disabled' : 'Enabled'}
                    </td>`
                )}
              </tr>
            </thead>
            <tbody>
              ${states.map(
                state => html` <tr>
                  <th>${state}</th>
                  ${disabledStates.map(
                    disabledState => html` <td class="${disabledState ? 'sb-disabled' : ''}">
                      <sl-select
                        ?valid=${state === 'valid'}
                        ?invalid=${state === 'invalid'}
                        ?required=${state === 'invalid'}
                        .size=${size}
                        ?disabled=${disabledState}
                        data-mock-state
                        >${options}
                      </sl-select>
                    </td>`
                  )}
                </tr>`
              )}
              ${states.map(
                state => html`<tr>
                  <th>Placeholder ${state}</th>
                  ${disabledStates.map(
                    disabledState => html`
                      <td class="${disabledState ? 'sb-disabled' : ''}">
                        <sl-select
                          ?valid=${state === 'valid'}
                          ?invalid=${state === 'invalid'}
                          ?required=${state === 'invalid'}
                          .size=${size}
                          ?disabled=${disabledState}
                          data-mock-state
                          placeholder="Placeholder"
                          ><sl-select-option .size=${size} ?disabled=${disabledState}>Hamster</sl-select-option>
                        </sl-select>
                      </td>
                    `
                  )}
                </tr>`
              )}
              <tr>
                <th>Unselected Option</th>
                ${disabledStates.map(
                  disabledState => html`
                    <td class="${disabledState ? 'sb-disabled' : ''}">
                      <sl-select-option .size=${size} ?disabled=${disabledState}>üêπ Hamster</sl-select-option>
                    </td>
                  `
                )}
              </tr>
              <tr>
                <th>Selected Option</th>
                ${disabledStates.map(
                  disabledState => html`
                    <td class="${disabledState ? 'sb-disabled' : ''}">
                      <sl-select-option .size=${size} ?disabled=${disabledState} selected>üêπ Hamster</sl-select-option>
                    </td>
                  `
                )}
              </tr>
            </tbody>
          </table>`
      )}`;
  }
}

fields.default:
{
    attributes: true,
    subtree: true,
    attributeFilter: ['selected', 'size'],
    attributeOldValue: true
  }

new RovingTabindexController<SelectOption>(this, {
    focusInIndex: (elements: SelectOption[]) => elements.findIndex(el => el.selected && isPopoverOpen(this.dialog)),
    elements: () => this.allOptions || [],
    isFocusableElement: (el: SelectOption) => !el.disabled
  })
